import React from 'react';

function Main() {

    const handleEditAvatarClick = () => {
        document.querySelector('.popup_type_avatar').classList.add('popup__opened');
    }

    const handleEditProfileClick = () => {
        console.log('123');
        document.querySelector('.popup_type_profile').classList.add('popup__opened');
    }

    return (
        <main className="main">
            <section className="profile">        
              <div className="profile__image" >
                <div className="profile__overlay">
                  <div className="profile__overlay-image" onClick={handleEditAvatarClick}>
                  </div>
                </div>
              </div>
              <div className="profile__info">
                <div className="profile__container">
                  <h1 className="profile__name"></h1>
                  <button className="profile__edit-button" type="button" aria-label="Редактировать профиль" onClick={handleEditProfileClick}></button>
                </div>
                <p className="profile__subscribe"></p>
              </div>
              <button className="profile__add-button" type="button" aria-label="Добавить информацию"></button>
            </section>
            <section className="elements">
            </section>
        </main>
    )
}

export default Main;
