import React, { useContext } from 'react';
import { CurrentUserContext } from '../contexts/CurrentUserContext.js';

function Card(props) {
  const currentUser = useContext(CurrentUserContext);
  const isOwn = props.card.owner._id === currentUser.id;
  const isLiked = props.card.likes.some(item => item._id === currentUser.id);

  const handleCardClick = () => {
      props.onCardClick(props.card);
  };

  const handleLike = () => {
    props.onLikeClick(props.card);
  };

  return(
      <article className="element">
        <img className="element__photo" alt={props.card.name} onClick={handleCardClick} style={{ backgroundImage: `url(${props.card.link})` }} />
        {isOwn ? <button className="element__delete-button" type="button" aria-label="Удалить"></button> : ''}
        <div className="element__description">
          <h2 className="element__title">{props.card.name}</h2>
          <div className="element__likes-container">
              <button 
              className={`element__like-button ${isLiked ? "element__like-button_active" : ''}`}
              type="button" 
              aria-label="Нравится" 
              onClick={handleLike}>
              </button>
              <p className="element__likes-amount">{props.card.likes.length}</p>
          </div>
        </div>
      </article>
  )
}

export default Card;
