import React, { useState, useEffect } from 'react';
import { api } from '../utils/Api.js';


function Main({onAddPlace, onEditAvatar, onEditProfile}) {

    const [userName, setUserName] = useState('');
    const [userDescription, setUserDescription] = useState('');
    const [userAvatar, setUserAvatar] = useState(null);

    useEffect(() => {
        api.getUserinfo().then(data => {
            setUserName(data.name);
            setUserDescription(data.about);
        });
    }, [])

    return (
        <main className="main">
            <section className="profile">        
              <div className="profile__image" >
                <div className="profile__overlay">
                  <div className="profile__overlay-image" onClick={onEditAvatar}>
                  </div>
                </div>
              </div>
              <div className="profile__info">
                <div className="profile__container">
                  <h1 className="profile__name"></h1>
                  <button className="profile__edit-button" type="button" aria-label="Редактировать профиль" onClick={onEditProfile}></button>
                </div>
                <p className="profile__subscribe"></p>
              </div>
              <button className="profile__add-button" type="button" aria-label="Добавить информацию" onClick={onAddPlace}></button>
            </section>
            <section className="elements">
            </section>
        </main>
    )
}

export default Main;
