import React, { useCallback, useContext, useState } from 'react';
import { Redirect } from 'react-router-dom';
import { UserDataContext } from '../contexts/UserDataContext.js';

function Login(props) {
  const userData = useContext(UserDataContext);

  const [email, setEmail] = useState(userData.email);
  const [password, setPassword] = useState(userData.password);

  const cbSubmit = useCallback((e) => {
    e.preventDefault();
    try {
      props.onLogin(userData.email, userData.password);
    }
    catch {}
  }, [props.onLogin, userData])

  function handleEmailChange(e) {
      setEmail(e.target.value);
  }

  function handlePasswordChange(e) {
      setPassword(e.target.value);
  }

  if (!props.isLoggedIn) {
    <Redirect to="/" />
  }


    return (
        <div className="auth">
            <div className="auth__container">
              <h2 className="auth__title">Вход</h2>
              <form className="auth__form" name="login-form" onSubmit={cbSubmit}>
                  <input className="auth__input" name="email" value={email} required type="email" placeholder="Email" onChange={handleEmailChange} />
                  <input className="auth__input" name="password" value={password} required type="password" placeholder="Пароль" onChange={handlePasswordChange} />
                <button className="auth__submit-button" type="submit" aria-label="Войти">Войти</button>
              </form>
            </div>
        </div>

    )
}

export default Login;
