import React from 'react';

function InfoTooltip(props) {
    return (
        <div className={`popup_type_register ${props.isOpen ? 'popup_opened' : ''}`}>
        <div className="popup__info-container">
          <button className="popup__close-button" type="button" aria-label="Закрыть" onClick={props.onClose} />
          <h2 className="popup__info-title">{props.title}</h2>
          <img className="popup__info-image" src={props.image} alt="Успешная регистрация"/>
        </div>
    </div>
    )
}

export default InfoTooltip;
