import React from 'react';

function Login(props) {
    return (
        <div className="auth">
            <div className="auth__container">
              <h2 className="auth__title">Вход</h2>
              <form className="auth__form" name="login-form">
                <div className="auth__input-zone">
                  <input className="auth__input auth__input_type_email" required type="email" placeholder="Email"/>
                  <input className="auth__input auth__input_type_password" required type="text" minLength="8" placeholder="Пароль"/>
                </div>
              <button className="auth__submit-button" type="submit" aria-label="Войти">Войти</button>
            </form>
            </div>
        </div>

    )
}

export default Login;
