import React, { useState, useEffect, useContext } from 'react';
import PopupWithForm from './PopupWithForm.js';
import { CurrentUserContext } from '../contexts/CurrentUserContext.js';

function EditProfilePopup(props) {
    const currentUser = useContext(CurrentUserContext);

    const [name, setName] = useState('');
    const [description, setDescription] = useState('');

    useEffect(() => {
        setName(currentUser.name);
        setDescription(currentUser.about);
      }, [currentUser]);

    function handleSubmit(e) {
        e.preventDefault();

        props.onUpdateUser({
          name,
          about: description,
        });
    }

    return (
        <PopupWithForm name="profile" title="Редактировать профиль" isOpen={props.isOpen} onClose={props.onClose} onSubmit={handleSubmit}>
          <form className="popup__form" name="profile-form" noValidate onSubmit={props.onSubmit}>
            <div className="popup__input-zone">
              <input className="popup__input popup__input_type_name" required type="text" name={name} onChange={handleChangeName} minLength="2" maxLength="40" placeholder="Введите Ваше имя" />
              <span className="popup__error"></span>   
            </div>
            <div className="popup__input-zone">
              <input className="popup__input popup__input_type_description" required type="text" name={description} minLength="2" maxLength="200" placeholder="Укажите род занятий"/>
              <span className="popup__error"></span> 
            </div>
            <button className="popup__submit-button" type="submit" aria-label="Сохранить">Сохранить</button>
          </form>
        </PopupWithForm>
    )
}

export default EditProfilePopup;
