import React, { useState, useContext, useRef } from 'react';
import { CurrentUserContext } from '../contexts/CurrentUserContext.js';
import { api } from '../utils/Api.js';

function Card(props) {
  const currentUser = useContext(CurrentUserContext);
  const likeButtonRef = useRef();
  const [isLiked, setIsLiked] = useState(props.card.likes.some(item => i._id === currentUser._id));
  const isOwn = props.card.owner._id === currentUser._id;

  const handleCardClick = () => {
      props.onCardClick(props.card);
  };

  function handleToggleLikeStatus() {
    likeButtonRef.current.classList.toggle('element__like-button_active');
  }

  const handleLikeButton = () => {
      if (isLiked) {
        api.deleteLike(props.card._id).then(() => {
          handleToggleLikeStatus();
        })
      } else {
       api.putLike(props.card._id).then(() => {
        handleToggleLikeStatus();
       })
      }
  };

  return(
      <article className="element">
        <img className="element__photo" alt={props.card.name} onClick={handleCardClick} style={{ backgroundImage: `url(${props.card.link})` }} />
        {isOwn ? <button className="element__delete-button" type="button" aria-label="Удалить"></button> : ''}
        <div className="element__description">
          <h2 className="element__title">{props.card.name}</h2>
          <div className="element__likes-container">
              <button 
              ref={likeButtonRef}
              className={`element__like-button ${isLiked ? 'element__like-button_active' : ''}`} 
              type="button" 
              aria-label="Нравится" 
              onClick={handleLikeButton}>
              </button>
              <p className="element__likes-amount">0</p>
          </div>
        </div>
      </article>
  )
}

export default Card;
