import React, { useState, useEffect } from 'react';
import { Route, Switch, withRouter } from 'react-router-dom';
import'../index.css';
import { CurrentUserContext } from '../contexts/CurrentUserContext.js';
import { CardsContext } from '../contexts/CardsContext.js';
import ProtectedRoute from './ProtectedRoute.js';
import Header from './Header.js';
import Login from './Login.js';
import Register from './Register.js';
import Main from './Main.js';
import Footer from './Footer.js';
import PopupWithForm from './PopupWithForm.js';
import ImagePopup from './ImagePopup.js';
import EditProfilePopup from './EditProfilePopup.js';
import EditAvatarPopup from './EditAvatarPopup.js';
import AddPlacePopup from './AddPlacePopup.js';
import { api } from '../utils/Api.js';
import * as auth from '../utils/Auth.js';
import success from '../images/success.svg';
import fail from '../images/fail.svg';
import InfoTooltip from './InfoTooltip';


function App() {
  const [isLoggenIn, setIsLoggeIn] = useState(false);
  const [userData, setUserData] = useState({});
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = useState(false);
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = useState(false);
  const [isInfoTooltipOpen, setIsInfoTooltipOpen] = useState(false);
  const [selectedCard, setSelectedCard] = useState(null);
  const [currentUser, setCurrentUser] = useState({name: '', about: ''});
  const [cards, setCards] = useState([]);

  useEffect(() => {
    api.getUserInfo().then(data => {
        setCurrentUser(data);
    })
    .catch((err) => {
      console.log(err);
    })
}, [])

  useEffect(() => {
    api.getInitialCards().then(cardList => {
        setCards(cardList);
    })
    .catch((err) => {
      console.log(err);
    })
  }, [])

  const handleLike = (card) => {
    if (card.likes.some(item => item._id === currentUser._id)) {
      api.deleteLike(card._id).then((newCard) => {
        setCards((cards) => cards.map((item) => card._id === item._id ? newCard : item));
      })
      .catch((err) => {
        console.log(err);
      })
    } else {
     api.putLike(card._id).then((newCard) => {
      setCards((cards) => cards.map((item) => card._id === item._id ? newCard : item));
     })
     .catch((err) => {
      console.log(err);
    })
    }
  };

  const handleCardDelete = (card) => {
      api.deleteCard(card._id).then(() => {
        setCards((cards) => cards.filter(item => item._id !== card._id))
      })
      .catch((err) => {
        console.log(err);
      })
  };

  const cbRegister = useCallback( async (email, password) => {
    try {
      const data = await auth.register(email, password);
      if (!data) {
        return (
          <InfoTooltip 
            title={"Что-то пошло не так! Попробуйте ещё раз."} 
            image={fail} 
            isOpen={isInfoTooltipOpen} 
            onClose={closeAllPopups} 
          />
        )
      }
      if (data.jwt) {
        setIsLoggeIn(true);
        setUserData(data.user);
        localStorage.setItem('jwt', data.jwt);
        return (
          <InfoTooltip 
            title={"Вы успешно зарегистрировались!"} 
            image={success} 
            isOpen={isInfoTooltipOpen} 
            onClose={closeAllPopups} 
          />
        )
      }
    } catch {}
  }, [])

function handleUpdateUser(name, about) {
  api.editUserInfo(name, about).then(data => {
    setCurrentUser(data);
    closeAllPopups();
  })
  .catch((err) => {
    console.log(err);
  })
}

function handleUpdateAvatar(avatar) {
  api.editUserAvatar(avatar).then(data => {
    setCurrentUser(data);
    closeAllPopups();
  })
  .catch((err) => {
    console.log(err);
  })
}

function handleAddPlaceSubmit(name, link) {
  api.addCard({
    name: name,
    link: link
  }).then(newCard => {
    setCards([newCard, ...cards]);
    closeAllPopups();
  })
  .catch((err) => {
    console.log(err);
  })
}

  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpen(!isEditAvatarPopupOpen);
  }

  function handleEditProfileClick() {
    setIsEditProfilePopupOpen(!isEditProfilePopupOpen);
  }

  function handleAddPlaceClick() {
    setIsAddPlacePopupOpen(!isAddPlacePopupOpen);
  }

  function handleRegisterSubmit() {
    console.log(123);
    setIsInfoTooltipOpen(!isInfoTooltipOpen);
  }

  function closeAllPopups() {
    setIsEditProfilePopupOpen(false);
    setIsAddPlacePopupOpen(false);
    setIsEditAvatarPopupOpen(false);
    setSelectedCard(null);
  }

  return (
    <CurrentUserContext.Provider value={currentUser}>
      <CardsContext.Provider value={cards}>
      <div className="App">
        <Header />
        <Switch>
          <ProtectedRoute 
            exact 
            path="/"
            component={Main}
            onAddPlace={handleAddPlaceClick} 
            onEditAvatar={handleEditAvatarClick} 
            onEditProfile={handleEditProfileClick} 
            onCardClick={setSelectedCard}
            onCardLike={handleLike}
            onCardDelete={handleCardDelete}
          />
          <Route path="/signup">
            <Register onSubmit={cbRegister} />
          </Route>
          <Route path="/signin">
            <Login />
          </Route>
        </Switch>

        <Footer />
        <EditProfilePopup isOpen={isEditProfilePopupOpen} onClose={closeAllPopups} onUpdateUser={handleUpdateUser} />
        <AddPlacePopup isOpen={isAddPlacePopupOpen} onClose={closeAllPopups} onAddPlace={handleAddPlaceSubmit} />
        <PopupWithForm name="delete-confirmation" title="Вы уверены?" onClose={closeAllPopups}>
          <button className="popup__submit-button" type="submit" aria-label="Да">Подтвердить</button>
        </PopupWithForm>
        <EditAvatarPopup isOpen={isEditAvatarPopupOpen} onClose={closeAllPopups} onUpdateAvatar={handleUpdateAvatar} />
        <ImagePopup card={selectedCard} onClose={closeAllPopups} />
      </div>
      </CardsContext.Provider>
    </CurrentUserContext.Provider>
  );
}

export default withRouter(App);
